    for i in range(len(pixel_list)):
        if pixel_list[i] > 0:
            current_obj.append(pixel_list[i])
        else:
            if len(current_obj) > 0:
                obj.append({'start_index': i - len(current_obj), 'end_index': i,'size': len(current_obj)})
