    return arr[x][y]

def get_values(arr, x, y, z=None):
    if z == None:
        return arr[x][y]
    else:
        return arr[x][y][z]

def set_value(arr, value, x, y):
    arr[x][y] = value

def set_values(arr, value, x, y, z=None):
    if z == None:
        arr[x][y] = value
    else:
