'''

import numpy as np
from skimage import io
import matplotlib.pyplot as plt

def get_pixel(img):
    return img[0][0]

def get_size(obj):
    return len(obj)

def get_start_index(obj):
    return obj[0].start

def get_end_index(obj):
    return obj[-1].end + 1

def get_color(obj):
    return obj[0].color

def get_noise(seq):
    noise = []
    for i in range(len(seq)):
        if seq[i] == 0:
            noise.append([])
    return noise

def get_objects(seq):
    objects = []
    current_object = []
    for i in range(len(seq)):
        if seq[i]!= 0:
            current_object.append({'start': i, 'end': i+1, 'color': seq[i]})
        else:
            if len(current_object) > 0:
                objects.append(current_object)
                current_object = []
    return objects

def get_distance(a, b):
    return abs(get_start_index(a)-get_start_index(b))

def get_alignment(a, b):
    return (get_start_index(a)+get_size(a)/2) - (get_start_index(b)+get_size(b)/2)

def get_containment(a, b):
    return ((get_start_index(a) >= get_start_index(b)) and (get_end_index(a) <= get_end_index(b)))

def get_hull(a, b):
    return [(min(get_start_index(a), get_start_index(b)), max(get_end_index(a), get_end_index(b)))]

def get_intersection(a, b):
    return [(max(get_start_index(a), get_start_index(b)), min(get_end_index(a), get_end_index(b)))]

def get_union(a, b):
    return [(min(get_start_index(a), get_start_index(b)), max(get_end_index(a), get_end_index(b)))]

def get_difference(a, b):
    return [(max(get_start_index(a), get_start_index(b)), min(get_end_index(a), get_end_index(b)))]

def get_symmetric_difference(a, b):
    return [(min(get_start_index(a), get_start_index(b)), max(get_end_index(a), get_end_index(b)))]

def get_count(seq):
    count = 0
    for i in range(len(seq)):
        if seq[i]!= 0:
            count += 1
    return count

def get_sort(seq):
    sorted_list = []
    for i in range(len(seq)):
        if seq[i]!= 0:
            sorted_list.append((i, seq[i]))
    sorted_list = sorted(sorted_list, key=lambda x:x[0])
    return [item[1] for item in sorted_list]

def get_conditional(condition, true_value, false_value):
    if condition:
        return true_value
    else:
        return false_value

def get_result():
    result = {}
    result['description'] = {}
    result['example_1'] = 'In this example, the object at index 5 was removed.'
    result['example_2'] = 'In this example, the object at index 6 was moved one place to the left.'
    result['example_3'] = 'In this example, the object at index 7 was scaled up by two places.'
