        if get_pixels(img)[i]!= 0:
            object_start = i
            while True:
                i += 1
                if get_pixels(img)[i] == 0:
                    object_end = i - 1
                    break
            objects[str(get_pixels(img)[object_start])] = {"start": object_start, "end": object_end}
