output: [0, 0, 0, 0, 0, 4, 0, 0, 0, 0, 0, 0, 0, 0, 4, 0]
"""
import numpy as np
from PIL import Image
import matplotlib.pyplot as plt

def get_image(img):
    img = Image.open(img)
    return np.array(img).astype('int')

def get_pixels(img):
    return img[0][0].tolist()

def get_size(img):
    return len(get_pixels(img))

def get_noise(img):
    noise = []
    for i in range(len(img)):
        if img[i] == 0:
            noise.append([])
        else:
            noise[-1].append(i)
    return noise

def get_objects(img):
    objects = {}
    noise = get_noise(img)
    for i in range(len(noise)):
        if len(noise[i]) > 0:
            objects['Object_{}'.format(i+1)] = {
               'start_index' : noise[i][0],
                'end_index'   : noise[i][-1]+1,
               'size'        : len(noise[i]),
                'color'       : img[noise[i][0]]
            }
    return objects

def get_distance(obj1, obj2):
    return abs(obj1['start_index'] - obj2['start_index']) + \
           abs(obj1['end_index'] - obj2['end_index'])

def get_alignment(obj1, obj2):
    return (abs(obj1['start_index'] - obj2['start_index']) < 1) and \
           (abs(obj1['end_index'] - obj2['end_index']) < 1)

def get_containment(obj1, obj2):
    return ((obj1['start_index'] >= obj2['start_index']) and \
             (obj1['end_index'] <= obj2['end_index'])) or \
           ((obj2['start_index'] >= obj1['start_index']) and \
             (obj2['end_index'] <= obj1['end_index']))

def get_reversal(obj1, obj2):
    return ((obj1['start_index'] >= obj2['start_index']) and \
             (obj1['end_index'] <= obj2['end_index'])) and \
           ((obj2['start_index'] >= obj1['start_index']) and \
             (obj2['end_index'] <= obj1['end_index']))

def get_count(objects):
    count = 0
    for key in objects:
        count += 1
    return count

def get_sort(objects):
    sorted_keys = sorted(list(objects), key=lambda x: objects[x]['start_index'], reverse=True)
    return sorted_keys

def get_hull(objects):
    hull = []
    for key in objects:
        hull.extend(range(objects[key]['start_index'], objects[key]['end_index']+1))
    return hull

def get_intersection(a, b):
    intersection = set(a).intersection(b)
    return list(intersection)

def get_difference(a, b):
    difference = set(a)-set(b)
    return list(difference)

def get_union(a, b):
    union = set(a).union(b)
    return list(union)

def get_complementary(a, b):
    complementary = set(a).symmetric_difference(b)
    return list(complementary)

def get_inverse(a, b):
    inverse = set(a).difference(b)
    return list(inverse)

def get_result(input_, objects, instructions):
    result = []
    for i in range(len(input_)):
        if input_[i]!= 0:
            result.append(input_[i])
        elif instructions['conditional'] == True:
            if instructions['condition']['type'] == 'equal':
                if instructions['condition']['value'] == objects[instructions['condition']['name']]['color']:
                    result.append(input_[i])
            elif instructions['condition']['type'] == 'greater than':
                if objects[instructions['condition']['name']]['color'] > instructions['condition']['value']:
                    result.append(input_[i])
            elif instructions['condition']['type'] == 'lesser than':
                if objects[instructions['condition']['name']]['color'] < instructions['condition']['value']:
                    result.append(input_[i])
            elif instructions['condition']['type'] == 'not equal':
                if objects[instructions['condition']['name']]['color']!= instructions['condition']['value']:
                    result.append(input_[i])
        else:
            result.append(input_[i])
    return result

