'''
import numpy as np
from PIL import Image
import matplotlib.pyplot as plt

def get_pixel(img):
    return img[0][0]

def get_size(obj):
    return obj['end_index'] - obj['start_index'] + 1

def get_distance(obj1, obj2):
    return abs(obj1['start_index'] - obj2['start_index'])

def get_alignment(obj1, obj2):
    if (get_distance(obj1, obj2) == 0):
        return True
    else:
        return False

def get_containment(obj1, obj2):
    if ((obj1['start_index'] >= obj2['start_index']) and (obj1['end_index'] <= obj2['end_index'])) or \
       ((obj1['start_index'] <= obj2['start_index']) and (obj1['end_index'] >= obj2['end_index'])) :
            return True
    else:
        return False

def get_recolor(obj, color):
    return [color]*get_size(obj)

def get_hollow(obj):
    return [0]*get_size(obj)

def get_scale(obj, factor):
    return [int(x/factor) for x in range(get_size(obj))]

def get_remove(obj):
    return [0]*get_size(obj)

def get_move(obj, offset):
    return [0]*offset+list(range(get_size(obj)))+[0]*(len(obj)-get_size(obj))

def get_copy(obj):
    return list(range(get_size(obj)))

def get_counting(obj):
    return [i+1 for i in range(get_size(obj))]

def get_sorting(obj):
    return sorted([i+1 for i in range(get_size(obj))])

def get_conditional(obj1, obj2, condition):
    if condition:
        return list(range(get_size(obj1)))
    else:
        return list(range(get_size(obj2)))

def get_noise(obj):
    return [np.random.randint(low=0, high=10)]*get_size(obj)

def get_relationship(obj1, obj2):
    if get_alignment(obj1, obj2):
        return get_counting(obj1)
    elif get_containment(obj1, obj2):
        return get_sorting(obj1)
    elif get_distance(obj1, obj2) > 0:
        return get_noise(obj1)
    else:
        return get_noise(obj2)

def get_transformation(obj1, obj2):
    if get_alignment(obj1, obj2):
        return get_counting(obj1)
    elif get_containment(obj1, obj2):
        return get_sorting(obj1)
    elif get_distance(obj1, obj2) > 0:
        return get_noise(obj1)
    else:
        return get_noise(obj2)

