'''
import numpy as np
from PIL import Image
import matplotlib.pyplot as plt

def get_pixel(img):
    return img[0][0]

def get_size(img):
    return len(img) * len(img[0])

def get_neighbours(img, x, y):
    neighbors = []
    if (x > 0):
        neighbors.append((x - 1, y))
    if (y > 0):
        neighbors.append((x, y - 1))
    if ((x < len(img)-1)):
        neighbors.append((x + 1, y))
    if ((y < len(img[0])-1)):
        neighbors.append((x, y + 1))
    return neighbors

def get_distance(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_center(obj):
    return int(len(obj)/2), int(len(obj[0])/2)

def get_area(obj):
    return len(obj)*len(obj[0])

def get_boundary(obj):
    boundary = []
    for i in range(len(obj)):
        boundary.extend([get_neighbours(obj, i, j) for j in range(len(obj[i]))])
    return set(boundary).difference({})

def get_intersection(a, b):
    intersection = []
    for i in range(min(len(a), len(b))):
        intersection.extend([get_neighbours(a, i, j) for j in range(len(a[i]))].intersection([get_neighbours(b, i, j) for j in range(len(b[i]))]))
    return intersection

def get_union(a, b):
    union = []
    for i in range(max(len(a), len(b))):
        union.extend([get_neighbours(a, i, j) for j in range(len(a[i]))]).union([get_neighbours(b, i, j) for j in range(len(b[i]))])
    return union

def get_containment(a, b):
    return get_union(a, b) == get_union(b, a)

def get_alignment(a, b):
    return get_center(a)[0] == get_center(b)[0] and get_center(a)[1] == get_center(b)[1]

def get_hull(obj):
    hull = []
    for i in range(len(obj)):
        hull.extend([get_neighbours(obj, i, j) for j in range(len(obj[i]))])
    return hull

def get_perimeter(obj):
    perimeters = []
    for i in range(len(obj)):
        perimeters.extend([get_neighbours(obj, i, j) for j in range(len(obj[i]))])
    return perimeters

def get_orientation(a, b):
    return get_perimeter(a) == get_perimeter(b)

def get_scale(a, b):
    return get_area(a) / get_area(b)

def get_recolor(a, b):
    return get_pixel(a)!= get_pixel(b)

def get_move(a, b):
    return get_center(a) == get_center(b)

def get_remove(a, b):
    return get_hull(a) == get_hull(b)

def get_copy(a, b):
    return get_hull(a) == get_hull(b)

def get_count(a, b):
    return get_size(a) == get_size(b)

def get_sort(a, b):
    return get_pixel(a) <= get_pixel(b)

def get_conditional(a, b):
    return get_pixel(a) >= get_pixel(b)

def get_noise(a, b):
    return get_pixel(a) == 0

