'''

import numpy as np
from skimage import io
import matplotlib.pyplot as plt

def get_pixel(img):
    return img[0][0]

def get_size(img):
    return len(img) * len(img[0])

def get_noise(img):
    noise = []
    for i in range(len(img)):
        for j in range(len(img[i])):
            if img[i][j] == 0:
                noise.append([i, j])
    return noise

def get_objects(img):
    objects = {}
    for i in range(len(img)):
        for j in range(len(img[i])):
            if img[i][j]!= 0:
                object_start = (i, j)
                while True:
                    if img[i+1][j] == 0:
                        break
                    elif img[i+1][j]!= 0:
                        i += 1
                    else:
                        break
                while True:
                    if img[i][j+1] == 0:
                        break
                    elif img[i][j+1]!= 0:
                        j += 1
                    else:
                        break
                objects[str(img[i][j])] = {"start": object_start, "end": (i, j), "size": abs((i - object_start[0]), (j - object_start[1]))}
    return objects

def get_distance(obj1, obj2):
    x1, y1 = obj1["start"]
    x2, y2 = obj2["start"]
    dist = abs(x1 - x2) + abs(y1 - y2)
    return dist

def get_alignment(obj1, obj2):
    x1, y1 = obj1["start"]
    x2, y2 = obj2["start"]
    align = False
    if x1 == x2:
        align = True
    elif x1 > x2:
        align = True
    elif x1 < x2:
        align = True
    elif y1 == y2:
        align = True
    elif y1 > y2:
        align = True
    elif y1 < y2:
        align = True
    return align

def get_containment(obj1, obj2):
    x1, y1 = obj1["start"]
    x2, y2 = obj2["start"]
    cont = False
    if x1 <= x2 and x2 <= x1 + obj1["size"] and y1 <= y2 and y2 <= y1 + obj1["size"]:
        cont = True
    elif x1 >= x2 and x2 >= x1 - obj1["size"] and y1 >= y2 and y2 >= y1 - obj1["size"]:
        cont = True
    return cont

def get_overlap(obj1, obj2):
    x1, y1 = obj1["start"]
    x2, y2 = obj2["start"]
    over = False
    if x1 <= x2 and x2 <= x1 + obj1["size"] and y1 <= y2 and y2 <= y1 + obj1["size"]:
        over = True
    elif x1 >= x2 and x2 >= x1 - obj1["size"] and y1 >= y2 and y2 >= y1 - obj1["size"]:
        over = True
    return over

def get_count(objects):
    count = 0
    for key in objects:
        count += 1
    return count

def get_sort(objects):
    sort = sorted(list(objects))
    return sort

def get_recolor(objects):
    recolor = []
    for key in objects:
        recolor.append(key)
    return recolor

def get_hollow(objects):
    hollow = []
    for key in objects:
        hollow.append("0")
    return hollow

def get_scale(objects):
    scale = []
    for key in objects:
        scale.append(key)
    return scale

def get_move(objects):
    move = []
    for key in objects:
        move.append(key)
    return move

def get_remove(objects):
    remove = []
    for key in objects:
        remove.append("0")
    return remove

def get_conditional(objects):
    cond = []
    for key in objects:
        cond.append(key)
    return cond

def get_result(input_, objects, noise, pixel, size, instructions):
    result = []
    for i in range(size):
        for j in range(size):
            if i == 0 and j == 0:
                result.append(get_pixel(input_))
            elif i == 0 and j!= 0:
                result.append(get_noise(input_)[j-1][0])
            elif i!= 0 and j == 0:
                result.append(get_noise(input_)[i-1][1])
            elif i!= 0 and j!= 0:
                result.append(get_noise(input_)[abs(i)+abs(j)-1])
    return result

if __name__ == "__main__":
    # Input
    img = io.imread('./data/task_5/input.png')
    img = np.array(img).astype(int)
    print(f'Input image shape: {img.shape}\n')

    # Test case
    test_case = io.imread('./data/task_5/test_case.png')
    test_case = np.array(test_case).astype(int)
    print(f'Test case image shape: {test_case.shape}\n')

    # Get pixel
    pixel = get_pixel(img)
    print(f'\nPixel: {pixel}\n')

    # Get size
    size = get_size(img)
    print(f'Size: {size}\n')

    # Get noise
    noise = get_noise(img)
    print(f'Noise: {noise}\n')

    # Get objects
    objects = get_objects(img)
    print(f'Objects: {objects}\n')

    # Get results
    result = get_result(img, objects, noise, pixel, size, instructions)
    print(f'Result: {result}\n')
