import numpy as np
from PIL import Image

def get_pixel(img):
    return img[0][0]

def get_size(img):
    return len(img) * len(img[0])

def get_noise(img):
    noise = []
    for i in range(len(img)):
        for j in range(len(img[i])):
            if img[i][j] == 0:
                noise.append([i, j])
    return noise

def get_objects(img):
    objects = {}
    for i in range(len(img)):
        for j in range(len(img[i])):
            if img[i][j]!= 0:
                object_start = (i, j)
                while True:
                    try:
                        if img[i+1][j] == 0:
                            break
                        else:
                            i += 1
                    except IndexError:
                        break
                try:
                    if img[i][j+1] == 0:
                        break
                    else:
                        j += 1
                except IndexError:
                    break
                if img[i][j] == 0:
                    break
                elif img[i][j]!= img[i+1][j]:
                    break
                elif img[i][j]!= img[i][j+1]:
                    break
                elif img[i][j]!= img[i-1][j]:
                    break
                elif img[i][j]!= img[i][j-1]:
                    break
                elif img[i][j]!= img[i+1][j+1]:
                    break
                elif img[i][j]!= img[i-1][j-1]:
                    break
                elif img[i][j]!= img[i+1][j-1]:
                    break
                elif img[i][j]!= img[i-1][j+1]:
                    break
                else:
                    pass
                object_end = (i, j)
                objects[(object_start, object_end)] = {"color": img[object_start[0]][object_start[1]], "start_index": object_start, "end_index": object_end}
    return objects

def get_image(seq):
    img = Image.new("L", (len(seq), len(seq)))
    for i in range(len(seq)):
        for j in range(len(seq[i])):
            img.putpixel((i, j), seq[i][j])
    return img

