    df = pd.read_csv('./data/train.csv')
    X = df['input'].values
    y = df['output'].values
    return (X,y)

def get_labels(df):
    labels = []
    for i in range(len(df)):
        if len(set(df[i])) == 1:
            labels.append([])
        else:
            labels.append(list(sorted(set(df[i]), key=lambda x:x)))
    return labels

