def get_image():
    return Image.open('./data/images/task_1.png')

def get_mask():
    return Image.open('./data/masks/task_1.png').convert("L")

def get_labels():
    return Image.open('./data/labels/task_1.png').convert("P", palette=Image.ADAM7)

def get_label_map():
    label_map = {}
    labels = get_labels()
    for i in range(len(labels)):
        if labels[i]!= 0:
            label_map[labels[i]] = i + 1
    return label_map

