import numpy as np
from skimage import io
import matplotlib.pyplot as plt

def get_pixel(img):
    return img[0][0]

def get_size(obj):
    return len(list(filter(lambda x:x==get_pixel(obj), obj)))

def get_start_index(obj):
    return next((i for i, e in enumerate(obj) if e == get_pixel(obj)), None)

def get_end_index(obj):
    return next((len(obj)-i for i, e in reversed(enumerate(reversed(obj)) if e == get_pixel(obj))))

def get_color(obj):
    return obj[get_start_index(obj)]

def get_objects(seq):
    return [(get_color(o), get_start_index(o), get_end_index(o)+1, get_size(o)) for o in seq if get_size(o)>=2]

def get_noise_pixels(seq):
    return [e for e in seq if get_size(e)<2]

def get_distance(a, b):
    return abs(get_start_index(b)-get_start_index(a))+abs(get_end_index(b)-get_end_index(a))

def get_alignment(a, b):
    return (get_start_index(a)==get_start_index(b)) or (get_end_index(a)==get_end_index(b))

def get_containment(a, b):
    return ((get_start_index(a)<=get_start_index(b)) and (get_end_index(a)>=get_end_index(b))) or \
           ((get_start_index(a)<=get_start_index(b)) and (get_end_index(a)<=get_end_index(b))) or \
           ((get_start_index(a)>=get_start_index(b)) and (get_end_index(a)>=get_end_index(b))) or \
           ((get_start_index(a)>=get_start_index(b)) and (get_end_index(a)<=get_end_index(b)))

def get_hull(a, b):
    return min([get_start_index(a), get_start_index(b)], key=int) + max([get_end_index(a), get_end_index(b)]) - min([get_end_index(a), get_end_index(b)], key=int)

def get_intersection(a, b):
    return min([get_start_index(a), get_start_index(b)], key=int) + max([get_end_index(a), get_end_index(b)]) - min([get_end_index(a), get_end_index(b)], key=int)

def get_union(a, b):
    return min([get_start_index(a), get_start_index(b)], key=int) + max([get_end_index(a), get_end_index(b)]) - min([get_end_index(a), get_end_index(b)], key=int)

def get_difference(a, b):
    return min([get_start_index(a), get_start_index(b)], key=int) + max([get_end_index(a), get_end_index(b)]) - min([get_end_index(a), get_end_index(b)], key=int)

def get_symmetric_difference(a, b):
    return min([get_start_index(a), get_start_index(b)], key=int) + max([get_end_index(a), get_end_index(b)]) - min([get_end_index(a), get_end_index(b)], key=int)

def get_count(seq):
    return sum([1 for _ in seq])

def get_sort(seq):
    return sorted(seq, key=lambda x:x[0])

def get_conditional(seq, cond):
    return [e for e in seq if cond(e)]

def get_inverted(seq):
    return [e for e in seq if not cond(e)]

