"""
import numpy as np
from PIL import Image
import matplotlib.pyplot as plt

def get_image(img):
    img = Image.open(img)
    return img

def get_array(img):
    arr = np.asarray(img)
    return arr

def get_pixels(arr):
    pixels = []
    for i in range(len(arr)):
        pixels += list(arr[i])
    return pixels

def get_colors(pixels):
    colors = set()
    for px in pixels:
        if px!= 0:
            colors.add(px)
    return colors

def get_objects(pixels, colors):
    objects = {}
    for c in colors:
        obj = {"start": None, "end": None}
        for i in range(len(pixels)-1,-1,-1):
            if pixels[i] == c:
                obj["start"] = i+1
                break
        for j in range(len(pixels),0,-1):
            if pixels[j] == c:
                obj["end"] = j
                break
        objects[c] = obj
    return objects

def get_size(obj):
    return int((obj['end'] - obj['start']) + 1)

def get_distance(a, b):
    return abs(int(b['start']) - int(a['end']))

def get_alignment(a, b):
    return (get_distance(a, b) % get_size(a)) == 0

def get_containment(a, b):
    return ((a['start'] <= b['start']) and (a['end'] >= b['end'])) or \
           ((b['start'] <= a['start']) and (b['end'] >= a['end']))

def get_noise(pixels):
    noise = []
    for i in range(len(pixels)):
        if pixels[i] == 0:
            noise.append([i, 0])
    return noise

def get_conditional(objects, condition):
    result = []
    for o in objects:
        if eval(condition):
            result.append(o)
    return result

def get_result(objects, instructions):
    result = []
    for i in range(len(objects)):
        if len(instructions) > 0:
            for inst in instructions:
                if type(inst) == str:
                    result.extend(eval(inst))
                else:
                    result.extend(inst)
        elif len(objects) > 0:
            result.append(list(objects)[i]['start'])
    return result

def get_output(input_, objects, instructions):
    output = []
    for i in range(len(input_)):
        if i in objects:
            output.append(list(objects)[i]['end'])
        else:
            output.append(input_[i])
    return output

def get_test_output(input_, objects, instructions):
    output = []
    for i in range(len(input_)):
        if i in objects:
            output.append(list(objects)[i]['end'])
        else:
            output.append(input_[i]+1)
    return output

def get_description(objects, instructions):
    description = ""
    for o in objects:
        description += f'{o}: {{start_index: {objects[o][\'start\']}, end_index: {objects[o][\'end\']}, size: {get_size(objects[o])}}}\n'
    for inst in instructions:
        if type(inst) == str:
            description += f'\t{inst}\n'
        else:
            description += f'\t{inst[0]} {inst[1]}\n'
    return description

def get_instruction(objects, instructions):
    instruction = ""
    for inst in instructions:
        if type(inst) == str:
            instruction += f'{inst}\n'
        else:
            instruction += f'\t{inst[0]} {inst[1]}\n'
    return instruction

def get_transformation(input_, objects, instructions):
    transfomration = ""
    for o in objects:
        transfomration += f'{o}: {{start_index: {objects[o][\'start\']}, end_index: {objects[o][\'end\']}, size: {get_size(objects[o])}}}\n'
    for inst in instructions:
        if type(inst) == str:
            transfomration += f'\t{inst}\n'
        else:
            transfomration += f'\t{inst[0]} {inst[1]}\n'
    return transfomration

if __name__ == '__main__':
    # Input
    img = get_image('./data/task_1/input.png')
    arr = get_array(img)
    pixels = get_pixels(arr)
    colors = get_colors(pixels)
    objects = get_objects(pixels, colors)
    noise = get_noise(pixels)

    # Output
    output = get_output(pixels, objects, ['move'])
    test_output = get_test_output(pixels, objects, ['move'])

    # Description
    desc = get_description(objects, ['move'])

    # Instructions
    ins = get_instruction(['move'], ['move'])

    # Transformations
    trnsfm = get_transformation(pixels, objects, ['move'])

    # Result
    res = {'description': {'Example_1': desc, 'Example_2': desc, 'Example_3': desc}, 'overall_pattern': '', 'instructions': ins, 'transformation': {'input': pixels, 'object_description': desc, 'transformed_objects': desc, 'output': output}, 'test_output': test_output}

    print(res)
