    return (np.where(obj == get_pixel(obj))[0])[0], (len(obj) - np.where(obj == get_pixel(obj))[-1])

def get_position(obj):
    return ((get_start_end(obj)[0]+1)/2)-1, ((get_start_end(obj)[1]+1)/2)-1

