#include <iostream>
using namespace std;

void rotate(int** arr, int n) {
    for (int i = 0; i < n / 2; ++i) {
        for (int j = i; j < n - i - 1; ++j) {
            // save the current element in temp variable
            int temp = arr[i][j];

            // move elements from right to top
            arr[i][j] = arr[n-j-1][i];

            // move elements from bottom to right
            arr[n-j-1][i] = arr[n-i-1][n-j-1];

            // move elements from left to bottom
            arr[n-i-1][n-j-1] = arr[j][n-i-1];

            // assign saved value to left
            arr[j][n-i-1] = temp;
        }
    }
}

void printArray(int **arr, int n) {
    cout << "[" << endl;
    for (int i = 0; i < n; ++i) {
        cout << "\t";
        for (int j = 0; j < n; ++j) {
            cout << arr[i][j] << ", ";
        }
        cout << endl;
    }
    cout << "]" << endl;
}

int main() {
    int testCases;
    cin >> testCases;

    while (testCases--) {
        int n;
        cin >> n;

        int** arr = new int*[n];
        for (int i = 0; i < n; ++i) {
            arr[i] = new int[n];
            for (int j = 0; j < n; ++j) {
                cin >> arr[i][j];
            }
        }

        rotate(arr, n);
        printArray(arr, n);

        for (int i = 0; i < n; ++i) {
            delete[] arr[i];
        }
        delete[] arr;
    }

    return 0;
}
