public class Solution {
    public int[] solution(int []arr) {
        int start = -1;
        for (int i=0;i<arr.length-1;i++) {
            if (start == -1 && arr[i]!= arr[i+1]) {
                start = i;
            } else if (start >= 0 && arr[i]!= arr[i+1]) {
                arr[start] += arr[i];
                arr[i+1] = arr[start];
                start++;
            }
        }
        return Arrays.copyOfRange(arr, 0, start==-1?arr.length:start+1);
    }
}
