class Solution {
    public String[] replace(String[] arr) {
        if (arr == null || arr.length <= 0) return new String[0];

        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < arr.length; i++) {
            if (i > 0 && arr[i].equals(arr[i - 1])) {
                sb.append(arr[i]);
            } else {
                sb.setLength(0);
                sb.append(arr[i]);
                arr[i] = sb.toString();
            }
        }
        return arr;
    }
}
