// Solution 1:
function groupAnimals(arr) {
    let result = [];
    for (let i = 0; i < arr.length - 1; i++) {
        if (arr[i + 1]!== arr[i]) {
            result.push([arr[i]])
        } else {
            result[result.length - 1].push(arr[i]);
        }
    }
    result.push([arr[arr.length - 1]]);

    // console.log('result :>> ', result);

    let finalResult = [];
    while (result.length > 0) {
        const firstElm = result.shift();
        let nextElm = null;
        if (result.length > 0) {
            nextElm = result[0][0];
        }

        if (!nextElm || String.fromCharCode(firstElm[0].charCodeAt() + 1) === nextElm) {
            firstElm.push(...result.shift());
            if (result.length > 0 && String.fromCharCode(firstElm[0].charCodeAt() + 1)!== result[0][0]) {
                result.unshift(firstElm);
            }
        } else {
            finalResult.push(firstElm);
        }
    }

    return finalResult.flat().map((el, idx) => el.toUpperCase()).join("");
}

