class Solution {
    public String[] replace(String[] arr) {
        if (arr == null || arr.length <= 0) return new String[0];

        int len = arr.length;
        for (int i = 0; i < len - 1; i++) {
            if (arr[i].equals("a") && arr[i + 1].equals("c")) {
                arr[i] = "f";
            }
        }

        // check last element
        if (len > 0 && arr[len - 1].equals("a") && arr[len - 2].equals("c")) {
            arr[len - 1] = "f";
        }

        return arr;
    }
}

