class Solution {
    public String[] replace(String[] arr) {
        if (arr == null || arr.length <= 0) return new String[0];

        int len = arr.length;
        for (int i = 0; i < len; i++) {
            if (i!= len - 1 && arr[i].equals("a") && arr[i + 1].equals("c")) {
                arr[i] = "f";
            } else if (i == len - 1 && arr[i].equals("a") && arr[i - 1].equals("c")) {
                arr[i] = "f";
            }
        }

        return arr;
    }
}
