// Solution 1:
class Solution {
    public String[] replace(String[] input) {
        if (input == null || input.length < 2) return input;

        int len = input.length, i = 0, j = 0;
        char[] chars = new char[len];
        while (i < len) {
            if (input[i].equals("a")) {
                chars[j++] = 'a';
                i++;
            } else { // "c"
                int count = 0;
                while (i < len && input[i].equals("c")) {
                    count++;
                    i++;
                }
                if (count == 1) {
                    chars[j++] = 'h';
                } else if (count == 2) {
                    chars[j++] = 'j';
                } else {
                    for (int k = 0; k < count / 3; k++) {
                        chars[j++] = 'g';
                    }
                    if (count % 3 == 1) {
                        chars[j - 1] = 'h';
                    } else if (count % 3 == 2) {
                        chars[j - 1] = 'j';
                    }
                }
            }
        }

        String[] output = new String[len];
        for (int k = 0; k < len; k++) {
            output[k] = Character.toString(chars[k]);
        }
        return output;
    }
}

// Solution 2:
class Solution {
    public String[] replace(String[] input) {
        if (input == null || input.length < 2) return input;

        List<String> list = new ArrayList<>();
        for (String s : input) {
            switch (s) {
                case "a":
                    list.add("a");
                    break;
                case "c":
                    int count = 0;
                    while (list.size() > 0 && list.get(list.size() - 1).equals("c")) {
                        count++;
                        list.remove(list.size() - 1);
                    }
                    if (count == 1) {
                        list.add("h");
                    } else if (count == 2) {
                        list.add("j");
                    } else {
                        int rem = count % 3;
                        count -= rem;
                        for (int i = 0; i < count / 3; i++) {
                            list.add("g");
                        }
                        if (rem == 1) {
                            list.add("h");
                        } else if (rem == 2) {
                            list.add("j");
                        }
                    }
                    break;
            }
        }

        String[] output = new String[list.size()];
        for (int i = 0; i < list.size(); i++) {
            output[i] = list.get(i);
        }
        return output;
    }
}
