Sample Prompt:
Example_1:
input: ['.', 'i', 'i', 'i', '.', '.', 'i', '.', 'i', 'i', '.', 'i', '.']
output: ['.', 'd', 'd', 'd', '.', '.', 'a', '.', 'g', 'g', '.', 'a', '.']

Example_2:
input: ['.', '.', '.', 'i', '.', '.', 'i', 'i', '.', '.', '.', '.', '.']
output: ['.', '.', '.', 'a', '.', '.', 'g', 'g', '.', '.', '.', '.', '.']

Example_3:
input: ['.', '.', '.', 'i', 'i', 'i', '.', 'i', '.', '.', 'i', 'i', '.']
output: ['.', '.', '.', 'd', 'd', 'd', '.', 'a', '.', '.', 'g', 'g', '.']

Test case:
input: ['.', '.', '.', 'i', 'i', 'i', '.', '.', 'i', '.', '.', 'i', 'i']

###########################################################
Sample Outputs:
output: ['.', '.', '.', 'd', 'd', 'd', '.', '.', 'a', '.', '.', 'g', 'g']
*/

public class Solution {
    public String[] solution(String[] input) {
        int len = input.length;
	int i, j, k;
	for (i=0; i<len-2; i++) {
            if (input[i].equals(".")) continue;
            for (j=i+1; j<len-1; j++) {
                if (input[j].equals(".") ||!input[j].equals(input[i])) break;
            }
            if (j == len - 1 && input[j].equals(input[i])) {
                // all the same
                for (k=i; k<=j; k++) {
                    if (input[k].equals("i")) input[k] = "d";
                    else input[k] = "a";
                }
            }
            else if (j < len - 1 && input[j].equals(".")) {
                // middle is dot
                for (k=i; k<j; k++) {
                    if (input[k].equals("i")) input[k] = "d";
                    else input[k] = "a";
                }
            }
        }
        return input;
    }
}
