input: ['.', '.', '.', '.', '.', 'g', 'g', 'g', 'g', 'g', 'g', 'g', '.', '.', 'h', '.']
output: ['.', '.', '.', '.', '.', 'g', 'g', 'g', 'g', 'g', 'g', 'g', 'g', 'g', 'h', '.']

Example_2:
input: ['.', '.', 'e', 'e', 'e', 'e', 'e', 'e', '.', '.', '.', '.', '.', 'h', '.', '.']
output: ['.', '.', 'e', 'e', 'e', 'e', 'e', 'e', 'e', 'e', 'e', 'e', 'e', 'h', '.', '.']

Example_3:
input: ['f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', '.', '.', 'h', '.']
output: ['f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'f', 'h', '.']

Test case:
input: ['a', 'a', 'a', 'a', 'a', 'a', 'a', '.', '.', 'h', '.', '.', '.', '.', '.', '.']

###########################################################
Sample Outputs:
output: ['a', 'a', 'a', 'a', 'a', 'a', 'a', 'h', '.', 'h', '.', '.', '.', '.', '.', '.']
*/

public class Solution {
    public String pushDominoes(String dominoes) {
        int len = dominoes.length();
        char[] arr = new char[len];
        Arrays.fill(arr, '.');
        for (int i = 0; i < len; ++i) {
            if (dominoes.charAt(i)!= '.') {
                arr[i] = dominoes.charAt(i);
            }
        }
        // left and right are the index of last non-dot character before current position from left and right respectively
        int left = -1, right = len;
        for (int i = 0; i < len; ++i) {
            if (arr[i] == '.') continue;
            if (left == -1) {
                left = i;
            } else {
                if (arr[left] == 'l' && arr[i] == 'r') {
                    while (++left < i) {
                        arr[left] = 'R';
                    }
                } else if (arr[left] == 'r' && arr[i] == 'l') {
                    while (--right > i) {
                        arr[right] = 'L';
                    }
                    left = i;
                }
            }
        }
        return new String(arr);
    }
}
